import React, { useState } from 'react';
import { Button, Form, Table } from 'react-bootstrap';

function Viajes() {
  // Declaración del estado para almacenar los viajes y el formulario de solicitud
  const [viajes, setViajes] = useState([]);
  const [direccion, setDireccion] = useState('');

  // Función para crear una solicitud de viaje
  const handleSolicitud = (e) => {
    e.preventDefault();
    const id = viajes.length + 1;
    const pasajero = 'Nombre del pasajero'; // Puedes reemplazarlo con el nombre del pasajero real
    const conductor = 'Nombre del conductor'; // Puedes reemplazarlo con el nombre del conductor real
    const fecha = new Date().toLocaleString();
    const estado = 'En espera';
    setViajes([...viajes, { id, pasajero, conductor, direccion, fecha, estado }]);
    setDireccion('');
  };

  // Función para completar un viaje
  const handleCompletar = (id) => {
    const index = viajes.findIndex((viaje) => viaje.id === id);
    const viaje = { ...viajes[index], estado: 'Completado' };
    const nuevosViajes = [...viajes.slice(0, index), viaje, ...viajes.slice(index + 1)];
    setViajes(nuevosViajes);
  };

  // Renderizado del componente
  return (
    <div>
      <h2>Viajes</h2>
      {/* Formulario para crear una solicitud de viaje */}
      <Form onSubmit={handleSolicitud}>
        <Form.Group controlId="formDireccion">
          <Form.Label>Dirección del destino</Form.Label>
          <Form.Control type="text" placeholder="Ingresa la dirección del destino" value={direccion} onChange={(e) => setDireccion(e.target.value)} required />
        </Form.Group>
        <Button variant="primary" type="submit">Crear solicitud de viaje</Button>
      </Form>
      <br />
      {/* Tabla para mostrar los viajes */}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Pasajero</th>
            <th>Conductor</th>
            <th>Dirección</th>
            <th>Fecha</th>
            <th>Estado</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {/* Iteración sobre los viajes para mostrarlos en la tabla */}
          {viajes.map((viaje) => (
            <tr key={viaje.id}>
              <td>{viaje.id}</td>
              <td>{viaje.pasajero}</td>
              <td>{viaje.conductor}</td>
              <td>{viaje.direccion}</td>
              <td>{viaje.fecha}</td>
              <td>{viaje.estado}</td>
              {/* Botón para completar el viaje */}
              <td>
                {viaje.estado === 'En espera' && (
                  <Button variant="success" onClick={() => handleCompletar(viaje.id)}>Completar</Button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default Viajes;
